import Colors from '../../constants/Colors';

export const stardardScreenStyle = {
  flex: 1,
  marginTop: 4,
  backgroundColor: Colors.backGroundColor,
  borderTopLeftRadius: 20,
  borderTopRightRadius: 20,
  shadowColor: 'black',
  shadowOpacity: 0.26,
  shadowOffset: { width: 4, height: -3 },
  shadowRadius: 8,
  elevation: 25,
};
